import React, { useEffect, useRef } from 'react';
import * as faceapi from 'face-api.js';

const QuestionPaper = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Load models once
  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = '/models'; // public/models/
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      startVideo();
    };

    loadModels();
  }, []);

  // Start webcam
  const startVideo = () => {
    navigator.mediaDevices.getUserMedia({ video: {} })
      .then(stream => {
        videoRef.current.srcObject = stream;
      })
      .catch(err => console.error("Webcam access error:", err));
  };

  // Detect face every 100ms
  const handleVideoPlay = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;

    const displaySize = { width: video.videoWidth, height: video.videoHeight };
    faceapi.matchDimensions(canvas, displaySize);

    setInterval(async () => {
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const resized = faceapi.resizeResults(detections, displaySize);

      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      faceapi.draw.drawDetections(canvas, resized);
    }, 100);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', padding: '2rem' }}>
      {/* LEFT SIDE: QUESTION */}
      <div style={{ flex: 1 }}>
        <h2 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '2rem' }}>
          How many Keywords are there in C Programming language ?
        </h2>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', width: '300px' }}>
          {['32', '43', '23', '33'].map(option => (
            <button key={option} style={btnStyle}>{option}</button>
          ))}
        </div>
      </div>

      {/* RIGHT SIDE: WEBCAM + NAV */}
      <div style={rightPanelStyle}>
        <div style={{ position: 'relative', width: '100%' }}>
          <video
            ref={videoRef}
            autoPlay
            muted
            onPlay={handleVideoPlay}
            style={{ width: '100%', borderRadius: '16px' }}
          />
          <canvas
            ref={canvasRef}
            style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
          />
        </div>

        <div style={gridStyle}>
          {[...Array(10).keys()].map((n) => (
            <button
              key={n}
              style={{
                ...numBtnStyle,
                backgroundColor: n === 0 ? '#666' : '#a8c4f1',
              }}
            >
              {n + 1}
            </button>
          ))}
        </div>

        <button style={submitBtn}>Submit</button>
      </div>
    </div>
  );
};

export default QuestionPaper;

// Styles
const btnStyle = {
  padding: '0.75rem',
  border: '1px solid #ccc',
  borderRadius: '12px',
  backgroundColor: '#fff',
  fontSize: '1rem',
  fontWeight: '500',
};

const rightPanelStyle = {
  width: '400px',
  padding: '1rem',
  background: '#fff',
  borderRadius: '20px',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(5, 1fr)',
  gap: '10px',
  marginTop: '20px',
};

const numBtnStyle = {
  width: '50px',
  height: '50px',
  border: 'none',
  borderRadius: '8px',
  color: 'white',
  fontWeight: 'bold',
};

const submitBtn = {
  marginTop: '20px',
  padding: '0.5rem 2rem',
  background: 'linear-gradient(to right, #00f2fe, #4facfe)',
  border: 'none',
  borderRadius: '30px',
  color: 'black',
  fontWeight: 'bold',
  fontSize: '1rem',
};
